package com.syntax.class01;

public class PracticeVariables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		byte data1=8;  //-128 to 127
		
		short data2=25678;  //-32768 to 32767
		
		int data3=73623892; 
		
		long data4=10099839999999l;
		
		//to store floating numbers
		
		float data5=25.38f;
		double data6=25.3898;
		
		//represent yes or no(true or false)
		boolean data7=true;
		boolean data8=false;
		
		//to represent single character
		char data9='S';
		char data10='&';
		char data11='5';
		
		System.out.println(data1);
		System.out.println(data2);
		System.out.println(data3);
		System.out.println(data4);
		System.out.println(data5);
		System.out.println(data6);
		System.out.println(data7);
		System.out.println(data8);
		System.out.println(data9);
		System.out.println(data10);
		System.out.println(data11);
		
	}

}
