package com.syntax.class03;

public class DoubleComparision {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double value1=3.45;
		double value2=7.98;
		
		if (value1>value2) {
			System.out.println("Double value "+value1+" is larger than "+value2);
		
		}else {
			System.out.println("Double value "+value1+" is not larger than "+value2);
		}

	}

}
