package com.syntax.class03;

public class Recap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int num1;//declaration
		num1=11; //initialization/assign value
		num1=12; // reassign;
		// if I write int num1=12; it will give error; because we 
		//already declare it.
		
		int a=12;
		int b=5;
		System.out.println(a/b);
		
		//it will be 2, because the result is integer.
		
		
		System.out.println("Remainder= "+a%b);
	}

}  
