package com.syntax.class09;

public class PrintingPatterns2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		  /*7654321
			7654321
			7654321
			7654321
			
		  */
		
		
		for (int r=5; r>=1; r--) {  ///for rows
			for (int c=7; c>=1; c--) { ///for columns
				System.out.print(c);
			}
			
			System.out.println();
		}
		
		System.out.println("----------------------");
		
		
		
		
		
		
		
		

	}

}
